import React from "react";
import { View, Text, ScrollView } from "react-native";

export default function DietResult({route}){
  const { plan } = route.params;
  return (
    <ScrollView style={{padding:20}}>
      <Text style={{fontSize:20, fontWeight:"bold"}}>7-Day Plan</Text>
      {Object.keys(plan).map((dayKey,idx)=>{
        const day = plan[dayKey];
        return (
          <View key={dayKey} style={{marginVertical:10, padding:10, borderWidth:1}}>
            <Text style={{fontWeight:"bold"}}>DAY {idx+1} - {day.total_calories} kcal</Text>
            {Object.keys(day.meals).map(mn=>(
              <View key={mn}><Text style={{fontWeight:"600"}}>{mn}</Text>
                <Text>{day.meals[mn].items.join(", ")}</Text>
                <Text>Calories: {day.meals[mn].calories}</Text>
              </View>
            ))}
          </View>
        )
      })}
    </ScrollView>
  )
}
