import React, {useState} from "react";
import { View, Text, TextInput, Button, ScrollView } from "react-native";
import axios from "axios";

export default function HomeScreen({navigation}){
  const [name,setName] = useState("");
  const [age,setAge] = useState("30");
  const [height,setHeight] = useState("175");
  const [weight,setWeight] = useState("78");
  const [goal,setGoal] = useState("Weight Loss");
  const [region,setRegion] = useState("South India");
  const [cuisine,setCuisine] = useState("Vegetarian");

  const callApi = async () => {
    const payload = {
      name, age: parseInt(age), goal, height_cm: parseFloat(height), current_weight_kg: parseFloat(weight), region, cuisine_preference: cuisine
    };
    try{
      const res = await axios.post("http://10.0.2.2:8080/diet-plan", payload);
      navigation.navigate("DietResult", {plan: res.data.daily_plan});
    }catch(e){
      alert("API error: " + e.message);
    }
  };

  return (
    <ScrollView style={{padding:20}}>
      <Text style={{fontSize:22, fontWeight:"bold"}}>Diet Plan Demo</Text>
      <Text>Name</Text>
      <TextInput value={name} onChangeText={setName} style={{borderWidth:1, marginBottom:10}} />
      <Text>Age</Text>
      <TextInput value={age} onChangeText={setAge} keyboardType="numeric" style={{borderWidth:1, marginBottom:10}} />
      <Text>Height (cm)</Text>
      <TextInput value={height} onChangeText={setHeight} keyboardType="numeric" style={{borderWidth:1, marginBottom:10}} />
      <Text>Weight (kg)</Text>
      <TextInput value={weight} onChangeText={setWeight} keyboardType="numeric" style={{borderWidth:1, marginBottom:10}} />
      <Text>Goal</Text>
      <TextInput value={goal} onChangeText={setGoal} style={{borderWidth:1, marginBottom:10}} />
      <Button title="Get 7-day Plan" onPress={callApi} />
      <View style={{height:20}} />
      <Button title="Nutrition Analyzer" onPress={()=>navigation.navigate("Nutrition")} />
    </ScrollView>
  )
}
