import React, { useState } from "react";
import { View, Text, TextInput, StyleSheet, ScrollView, Pressable } from "react-native";
import axios from "axios";
import chapathi from '../assets/images/chapathi.png';
import { LinearGradient } from "expo-linear-gradient";

export default function NutritionScreen() {
  const [items, setItems] = useState("Chapathi: 4 pieces\nPotato Kuruma: 200 g");
  const [result, setResult] = useState(null);

  const callApi = async () => {
    const lines = items.split("\\n").map(l => {
      const parts = l.split(":");
      return { item: parts[0].trim(), quantity: (parts[1] || "1 unit").trim() }
    });
    try {
      const res = await axios.post("http://10.68.172.233:8080/nutrition", { foods: lines });
      setResult(res.data.meal_nutrition);
    } catch (e) {
      alert("API error: " + e.message);
    }
  };

  return (
    <ScrollView style={{ padding: 20 }}>
      <Text style={{ fontSize: 20, fontWeight: "bold" }} >Nutrition Analyzer</Text>
      <Text style={{ marginTop: "12px", marginBottom: "12px" }} >Enter one item per line like: Chapathi: 4 pieces</Text>
      <img src={chapathi} style={{ maxWidth: "372px", width: "100%", boxShadow: "0px 4px 21px -10px #054d7d", marginBottom: "18px", borderRadius: "38px", marginRight: "auto", marginLeft: "auto" }} alt="chapathi" />
      <TextInput value={items} style={{ fontSize: 16, alignSelf: "center", marginBottom: "22px", fontWeight: "700", color: "rgb(0,70,113)" }} onChangeText={setItems} />
      <View style={styles.buttonWrapper}>
        <Pressable title="Analyze" onPress={callApi}>
          <LinearGradient
            colors={["rgb(5 ,79 ,128)", "rgb(0 ,56, 91)"]}
            start={{ x: 0, y: 0 }}
            end={{ x: 1, y: 1 }}
            style={styles.button}
          >
            <Text style={styles.buttonText}>Generate 7-Day Plan</Text>
          </LinearGradient>
        </Pressable>
      </View>
      {result && (
        <View style={{ marginTop: 20 }}>
          <Text>Total Calories: {result.total_calories}</Text>
          <Text>Protein: {result.macros.protein} g</Text>
          <Text>Carbs: {result.macros.carbs} g</Text>
          <Text>Fat: {result.macros.fat} g</Text>
          <View style={{ height: 10 }} />
          {result.breakdown.map(b => (
            <View key={b.item} style={{ borderTopWidth: 1, paddingTop: 8 }}>
              <Text>{b.item}</Text>
              <Text>Calories: {b.calories} | P: {b.protein} | C: {b.carbs} | F: {b.fat}</Text>
            </View>
          ))}
        </View>
      )}
    </ScrollView>
  )
}

const styles = StyleSheet.create({
  buttonWrapper: {
    marginTop: 22,
    borderRadius: 6,
    boxShadow: "0px 0px 12px -4px rgb(32 158 239)",
    borderWidth: 1,
    borderColor: "rgb(152,214,255)",
    overflow: "hidden"
  },
  button: {
    paddingVertical: 15,
    alignItems: "center",
    justifyContent: "center",
  },
  buttonText: {
    color: "rgb(5, 86, 139)",
    fontSize: 16,
    fontWeight: "600",
  },


});
